import React from 'react'
import './Main.css';

const Main = (props) => {

  const {children,background,title}=props;

     const renderMain = () => {
        
      let cssClass ="main";
      if(background){
       cssClass ="mini-background"
      }

     return(
    <div className="{cssClass}">
      <div className='container-fluid'>
      <h2> {title}</h2>
        {children }
      </div>
      </div>
      )
     }

     console.log(background)
     return (
    <>
      {renderMain()
      }
    </>
  )
}

export default Main
